/**
 * Get route
 * Ex: {{ 'routeName' | url({'paramOne': valueOne, 'paramTwo': valueTwo}) }}
 *
 * @param name
 * @param params
 * @returns {string}
 */
module.exports = function(name, params) {
    var route = global.routes[name] || '';

    if (typeof params !== 'object') {
        route = route.replace('{', '').replace('}', '');
        return route;
    }
    for (var key in params) {
        route = route.replace('{'+key+'}', params[key] || '');
    }
    route = route.replace('{', '').replace('}', '');
    return route;
}